typedef unsigned char byte;

// Array de bytes donde se van a volcar los datos
byte bytesArray[8192];

typedef struct memoria {
  int posicion;
  byte * bytesArray;
} memoria;

void cargarByte(memoria **mem, byte dato) {
  int i = (*mem)->posicion;
  (*mem)->bytesArray[i] = dato;
  (*mem)->posicion += 1;
}

