void concatenar(char *s, char c) {
  if (strlen(s) < MAX_LONGITUD_CADENA + 2) { // Si cabe uno mas
    for (; *s; s++);
    *s++ = c;
    *s++ = '\0';
  }
}

void uppercase(char *s) {
  for (; *s; s++)
    *s = toupper(*s);
}

