// Devuelve nombre de archivo ejecutable
void getNombre(char *arg, char **output) {
    char *token;
    int len = 0;
    token = strtok(arg, ".");

    while (token[len] != '\0') {
        len++;
    }

    strcat(*output, token);
    strcat(*output, ".exe");
}
